package section04;

public class EX03 {
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num = 5;
      if (num > 4) {
         System.out.println(num + "는 4보다 큽니다.");
      } else {
         System.out.println(num + "은 4보다 작습니다.");
      // ▼▼ 결과값 ▼▼
      // 5는 4보다 큽니다.		-> if문만 참이므로 if문만 실행됨
         
      }
   }

	}
